import React, { Component } from 'react';
import {DraggableCard} from '../../presentational/molecules/Card';
import {Bord} from './Bord';
import {TextForm} from '../../presentational/atoms/TextForm'

class BordContainer extends Component {

  constructor(props) {
    super(props);
  }

  render () {
    const style = {
      background: "rgba(0,0,0,0.0)",
      display: "inline-block",
      verticalAlign: "top",
      overflowX: "scroll"
    }
    
    return (
    <div className="bordContainer" style={style}>
        {this.props.columns.map(column => (
          <Bord
            key={column.id}
            title={column.title}
            addCard={this.props.addCard.bind(null, column.id)}
          >
            {column.cardIds
              .map(cardId => this.props.cards.find(card => card.id === cardId))
              .map((card, index) => (
                <DraggableCard
                  key={card.id}
                  id={card.id}
                  columnId={column.id}
                  columnIndex={index}
                  title={card.title}
                  content={card.content}
                  moveCard={this.props.moveCard}
                />
            ))}

            {column.cardIds.length === 0 && (
              <DraggableCard
                isSpacer
                moveCard={cardId => this.props.moveCard(cardId, column.id, 0)}
              />
            )}
          </Bord>
        ))}
        <div className="col s4 m4" style={style}>
          <TextForm onSubmit={this.props.addColumn} placeholder="リストを追加" />
        </div>
    </div>
    )
  }
}

export default BordContainer;
