import React, {Component} from 'react';
import BordHeaderTitleLabel from '../atoms/BordHeaderTitleLabel';
import SantenButton from '../atoms/SantenButton';

class BordHeader extends Component {

  constructor(props) {
    super(props);
    this.state = {
      isShown: false
    }
  }

  Open = () => {
    this.setState({
      isShown: true
    });
  };

  Close = () => {
    this.setState({
      isShown: false
    });
  };

  render() {

    const style = {
      display: "flex",
      marginTop: "5px"
    }

    const popupStyle = {
      transform: "scale(0)",
      transformOrigin: "top left",
      transition: "transform 0.2s",
      backgroundColor: "#fff",
      borderRadius: "0.5rem"
    }

    const popupStyleShown = {
      transform: "scale(1)"
    }


    return (
      <div className="bordHeader" style={style}>
        <BordHeaderTitleLabel title={this.props.title} />
        <div className={popupStyle}>
          <div>menu</div>
          <button onClick={this.Close.bind(this)}>close menu</button>
        </div>
        <SantenButton label="…" onClick={this.Open.bind(this)}/>
      </div>
    )
  }
}

export default BordHeader;
