import React, { Component } from 'react';
import {DraggableCard} from '../../presentational/molecules/Card';
import {Bord} from './Bord';

class BordContainer extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [
        {id: 1, title: "aaa", content:"内容"},
        {id: 2, title: "bbb", content:"内容"},
        {id: 3, title: "ccc", content:"内容"}
      ]
    }
  }

  render () {
    const style = {
      width: "100%",
      background: "rgba(0,0,0,0.1)",
    }
    
    return (
    <div className="bordContainer" style={style}>
      <div className="row">
        {this.props.columns.map(column => (
          <Bord
            key={column.id}
            title={column.title}
            addCard={this.props.addCard.bind(null, column.id)}
          >
            {column.cardIds
              .map(cardId => this.props.cards.find(card => card.id === cardId))
              .map((card, index) => (
                <DraggableCard
                  key={card.id}
                  id={card.id}
                  columnId={column.id}
                  columnIndex={index}
                  title={card.title}
                  moveCard={this.props.moveCard}
                />
            ))}

            {column.cardIds.length === 0 && (
              <DraggableCard
                isSpacer
                moveCard={cardId => moveCard(cardId, column.id, 0)}
              />
            )}
          </Bord>
        ))}
      </div>
    </div>
    )
  }
}

export default BordContainer;
