import React, {Component} from 'react';
import BordHeaderTitleLabel from '../atoms/BordHeaderTitleLabel';
import SantenButton from '../atoms/SantenButton';

class BordHeader extends Component {

  constructor(props) {
    super(props);
    this.state = {
      isShown: false
    }
  }

  Open = () => {
    this.setState({
      isShown: true
    });

    // document.body.addEventListener('click', e => {
    //   this.setState({
    //     isShown: false
    //   });
    //   document.body.removeEventListener('click', documentClickHandler)
    // })
  };

  Close = () => {
    this.setState({
      isShown: false
    });
  };

  render() {

    const style = {
      display: "flex",
      marginTop: "5px",
      position: "relative"
    }

    const popupStyle = {
      position: "absolute",
      top: "20px",
      right: "-185px",
      zIndex: "2",

      width: "200px",
      padding: "1rem",
      backgroundColor: "#fff",
      borderRadius: "3px",

      transform: "scale(0)",
      transformOrigin: "top left",
      transition: "transform 0.05s"
    }

    return (
      <div className="bordHeader" style={style}>
        <BordHeaderTitleLabel title={this.props.title} />
        <div className="card" style={this.state.isShown ? {...popupStyle, transform: "scale(1)"} : popupStyle}>
          <div>menu</div>
          <button onClick={this.Close.bind(this)}>close menu</button>
        </div>
        <SantenButton label="…" onClick={this.Open.bind(this)}/>
      </div>
    )
  }
}

export default BordHeader;
