import React, { Component } from 'react';
import Bord from './Bord';

let _columnId = 0;
let _cardId = 0;

const initialCards = Array.from({length: 9}).map(() => ({
  id: ++_cardId,
  title: `Card ${_cardId}`,
}));

const initialColumns = ['TODO', 'Doing', 'Done'].map((title, i) => ({
  id: _columnId++,
  title,
  cardIds: initialCards.slice(i * 3, i * 3 + 3).map(card => card.id),
}));

class BordContainer extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [
        {id: 1, title: "aaa", content:"内容"},
        {id: 2, title: "bbb", content:"内容"},
        {id: 3, title: "ccc", content:"内容"}
      ]
    }
  }

  addColumn = _title => {
    const title = _title.trim();
    if (!title) return;

    const newColumn = {
      id: ++_columnId,
      title,
      cardIds: [],
    };
    this.setState(state => ({
      columns: [...state.columns, newColumn],
    }));
  };

  onDrop = (toId, fromId) => {
    const items = this.state.items.slice();
    const toIndex = items.findIndex(i => i.id === toId);
    const fromIndex = items.findIndex(i => i.id === fromId);
    const toItem = items[toIndex];
    const fromItem = items[fromIndex];
    items[toIndex] = fromItem;
    items[fromIndex] = toItem;
    this.setState({items})
  }

  findItem = (index) => {
    return this.state.items.filter(c => c.id === index)[0].id
  }

  render () {
    const style = {
      width: "100%",
      background: "rgba(0,0,0,0.1)",
    }
    
    return (
    <div className="bordContainer" style={style}>
      <div className="row">
        <Bord title="Todo" items={this.state.items} onDrop={this.onDrop.bind(this)} findItem={this.findItem.bind(this)}/>
        <Bord title="作業中" items={this.state.items} onDrop={this.onDrop.bind(this)} findItem={this.findItem.bind(this)}/>
        <Bord title="完了" items={this.state.items} onDrop={this.onDrop.bind(this)} findItem={this.findItem.bind(this)}/>
      </div>
    </div>
    )
  }
}

export default BordContainer;
