import React, { Component } from 'react';
import { DragDropContext } from 'react-dnd';
import HTML5Backend from 'react-dnd-html5-backend';
import Card from '../../presentational/molecules/Card';
import BordHeader from '../../presentational/molecules/BordHeader';



class Bord extends Component {

  constructor(props) {
    super(props);
  }

  render () {

    const style = {
      background: "rgba(0,0,0,0.3)",
      height: "800px",
      width: "300px",
      margin: "10px",
      borderRadius: "3px 3px 3px 3px"
    }

    return (
    <div className="col s4 m4" style={style}>
      <BordHeader title={this.props.title} />
      {
      this.props.items.map(item => {
        return (
        <Card key={item.id} id={item.id} onDrop={this.props.onDrop} title={item.title} content={item.content} findItem={this.props.findItem}/>
        )
      })
      }
    </div>
    );
  }
}

export default DragDropContext(HTML5Backend)(Bord);
