function RemoveButton(props) {

  const style = {
    marginLeft: "auto",
    color: "#333333"
  };

  onSubmit = event => {
    const form = event.target;
    event.preventDefault();
    this.props.onSubmit(form.input.value);
    form.reset();
  };

  return (
    <button onSubmit={props.onSubmit}>
      <i class="material-icons">remove</i>
    </button>
  );
}

export default RemoveButton;
