import React, {Component} from 'react';
import {DragDropContext} from 'react-dnd';
import HTML5Backend from 'react-dnd-html5-backend';
import BordContainer from './BordContainer';
import CardCreateContainer from './CardCreateContainer';

let _columnId = 0;
let _cardId = 0;

const initialCards = Array.from({length: 9}).map(() => ({
  id: ++_cardId,
  title: `Card ${_cardId}`,
}));

const initialColumns = ['TODO', 'Doing', 'Done'].map((title, i) => ({
  id: _columnId++,
  title,
  cardIds: initialCards.slice(i * 3, i * 3 + 3).map(card => card.id),
}));

class ContentContainer extends Component {

  constructor(props) {
    super(props);
    this.state = {
      cards: initialCards,
      columns: initialColumns,
    }
  }

  render() {
    const style = {
      display: "flex",
      width: "100%",
      height: "883px",
      background: "lightblue"
    }
    return (
      <div className="contentContainer" style={style}>
        <CardCreateContainer />
        <BordContainer />
      </div>
    )
  }
}

export default DragDropContext(HTML5Backend)(ContentContainer);
