import React, { Component } from 'react';
import { DragSource, DropTarget } from 'react-dnd';
import TitleLabel from '../atoms/TitleLabel';
import ContentLabel from '../atoms/ContentLabel';

const dragSource = DragSource(
  "item", 
  {
    beginDrag(props) {
      return {
        id: props.id,
        title: props.title,
        originalIndex: props.findItem(props.id)
      };
    },
    endDrag(props: CardProps, monitor: DragSourceMonitor) {
      const { id, originalIndex } = monitor.getItem()
      const didDrop = monitor.didDrop()
      if (!didDrop) {
        props.onDrop(id, originalIndex)
      }
    }
  },
  (connect, monitor) => {
    return {
      connectDragSource: connect.dragSource(),
      connectDragPreview: connect.dragPreview(),
      isDragging: monitor.isDragging()
    };
  }
)

const dropTarget = DropTarget(
  "item",
  {
    canDrop() {
      return false
    },
    hover(props: CardProps, monitor: DropTargetMonitor) {
      const draggedId = monitor.getItem().id
      const overId = props.id
      if (draggedId !== overId) {
        const overIndex = props.findItem(overId)
        props.onDrop(draggedId, overIndex)
      }
    }
  },
  connect => {
    return {
      connectDropTarget: connect.dropTarget()
    };
  }
)

class Card extends Component {

  constructor(props) {
    super(props)
  }

  getItemStyles() {
    const { isDragging } = this.props
    return {
      opacity: isDragging ? 0.6 : 1,
    }
  }

  render() {
    return (
      this.props.connectDragSource(
        this.props.connectDropTarget(
          <div className="card">
            <div className="card-content">
              <TitleLabel title={this.props.title} />
              <ContentLabel content={this.props.content} />
            </div>
          </div>
        )
      )
    )
  }
}

export default dragSource(dropTarget(Card));
