import React, { Component } from 'react';
import BordHeader from '../../presentational/molecules/BordHeader';
import {TextForm} from '../../presentational/atoms/TextForm'

class Bord extends Component {

  constructor(props) {
    super(props);
  }
  
  render () {

    const style = {
      background: "rgba(200,200,200,0.2)",
      margin: "5px",
      borderRadius: "3px 3px 3px 3px",
      display: "inline-block",
      verticalAlign: "top",
      padding: "8px"
    }

    return (
      <div className="bord" style={style}>
        <BordHeader title={props.title} />
        {props.children}
        <button class="btn waves-effect waves-light deep-purple lighten-3" type="submit" name="action">カードを追加
          <i class="material-icons right">add</i>
        </button>
        <TextForm onSubmit={props.addCard} placeholder="カードを追加" />
      </div>
    );
  }
}

export default Bord;
