import React, { Component } from 'react';
import BordHeader from '../../presentational/molecules/BordHeader';
import {TextForm} from '../../presentational/atoms/TextForm'

const style = {
  background: "rgba(0,0,0,0.1)",
  margin: "5px",
  borderRadius: "3px 3px 3px 3px",
  display: "inline-block",
  verticalAlign: "top",
  padding: "8px"
}

export function Bord(props) {
  return (
    <div className="bord" style={style}>
      <BordHeader title={props.title} />
      {props.children}
      <TextForm onSubmit={props.addCard} placeholder="カードを追加" />
    </div>
  );
}
