import React, { Component } from 'react';
import { DragDropContext } from 'react-dnd';
import HTML5Backend from 'react-dnd-html5-backend';
import Card from '../../presentational/molecules/Card';
import BordHeader from '../../presentational/molecules/BordHeader';



class Bord extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [
        {id: 1, title: "aaa", content:"内容"},
        {id: 2, title: "bbb", content:"内容"},
        {id: 3, title: "ccc", content:"内容"}
      ],
    }
  }

  onDrop = (toId, fromId) => {
    const items = this.state.items.slice();
    const toIndex = items.findIndex(i => i.id === toId);
    const fromIndex = items.findIndex(i => i.id === fromId);
    const toItem = items[toIndex];
    const fromItem = items[fromIndex];
    items[toIndex] = fromItem;
    items[fromIndex] = toItem;
    this.setState({items})
  }

  findItem = (index) => {
    return this.state.items.filter(c => c.id === index)[0].id
  }

  componentDidMount() {
    // Preview表示する時のみ、元のComponentの描画を消す
    if (this.props.connectDragPreview) {
      this.props.connectDragPreview(getEmptyImage(), {
        captureDraggingState: true,
      });
    }
  }

  render () {

    const style = {
      background: "rgba(0,0,0,0.3)",
      height: "800px",
      width: "300px",
      margin: "10px",
      borderRadius: "3px 3px 3px 3px"
    }

    return (
    <div className="col s4 m4" style={style}>
      <BordHeader title={this.props.title} />
      {
      this.state.items.map(item => {
        return (
        <Card key={item.id} id={item.id} onDrop={this.onDrop.bind(this)} title={item.title} content={item.content} findItem={this.findItem.bind(this)}/>
        )
      })
      }
    </div>
    );
  }
}

export default DragDropContext(HTML5Backend)(Bord);
